let moduleMap = {};
getModulesQuery.data.forEach(moduleInfo => {
    let moduleEntry = moduleMap[moduleInfo.module_name];
    if(!moduleEntry) {
        moduleEntry = {};
        moduleEntry.moduleName = moduleInfo.module_name;
        moduleEntry.displayName = moduleInfo.display_name;
        moduleEntry.shortDesc = moduleInfo.short_description;
        moduleEntry.versions = [];
        moduleMap[moduleInfo.module_name] = moduleEntry;
    }

    versionEntry = {};
    versionEntry.version = moduleInfo.module_version;
    if(requestParams.moduleType == "es") {
        if(moduleInfo.es_url) versionEntry.esUrl = moduleInfo.es_url;
        if(moduleInfo.es_workspaces) {
            try {
                let esWorkspaces = JSON.parse(moduleInfo.es_workspaces);
                versionEntry.esDemoWorkspaces = esWorkspaces;
            }
            catch(error) {
                //uh, throw this in...
                versionEntry.esDemoWorkspacesError = error.toString();
            }
        }
    }
    else if(requestParams.moduleType == "npm") {
        if(moduleInfo.npm_url) versionEntry.esUrl = moduleInfo.npm_url;
        if(moduleInfo.npm_workspaces) {
            try {
                let npmWorkspaces = JSON.parse(moduleInfo.npm_workspaces);
                versionEntry.npmDemoWorkspaces = npmWorkspaces;
            }
            catch(error) {
                //uh, throw this in...
                versionEntry.npmDemoWorkspacesError = error.toString();
            }
        }
    }
    else {
        throw new Error("Unrocognized module type: " + requestParams.moduleType);
    }
    if(moduleInfo.web_link) versionEntry.webLink = moduleInfo.web_link;

    moduleEntry.versions.push(versionEntry);
})
return moduleMap;

