return data;

dbFileForm.layoutCode:
let onSubmit = () => {
    var {dialog} = require('electron').remote;

    let pathPromise = dialog.showOpenDialog({properties: ['openFile']})
        .then( fileOpenResult => {
            if(fileOpenResult.canceled) {
                //canceled - no action
                return;
            }
        	else if(fileOpenResult.filePaths.length === 1) {
        	    commandMessenger.dataCommand("dbFileForm.value",fileOpenResult.filePaths[0]);
        	}
        	else if(fileOpenResult.filePaths.length > 1) {
        	    apogeeUserAlert("Multiple file selection not supported!");
        	}
        	else {
        	    //canceled - no action
        		apogeeUserAlert("Unknown error: no path selected");
        	}
        })
        .catch(err => {
        	apogeeUserAlert("Error getting DB path: " + err.toString());
        });
}

return [
    {
        type: "submit",
        onSubmit: onSubmit,
        submitLabel: "Select DB File"
    }    
]

